$(function () { $(".navbar__item").on("click", e => toggleActive(e.currentTarget.id)), $(".dropdown__item").on("click", e => toggleActive(e.currentTarget.id)) }); const toggleActive = e => { $(`#${e}`).hasClass("dropdown") || $(".active").removeClass("active"), $(`#${e}`).addClass("active"), $("a>span:first-child").removeClass("text--red"), $(`#${e}>a>span:first-child`).addClass("text--red") };